<?phpnamespace frontend\controllers;use common\helpers\Visitors;use common\models\Article;use yii\web\Controller;use yii;class JobController extends CommonController{//    /**////     * @var string////     */////    public $layout = 'main';//    public function behaviors()//    {////        return [//            [//                'class' => 'yii\filters\PageCache',//                'only' => ['index'],//                'duration' => 3600,//                'variations' => [//                    Yii::$app->language,//                ],////            ],////        ];//    }    public function actionIndex()    {        parent::common();        $list = Article::find()            ->where(['category_id'=>64])            ->orderBy("np desc,create_time desc")            ->andWhere(['status'=>1]);        $dataProvider = new \yii\data\ActiveDataProvider([            'query' => $list,            'pagination' => [                'pageSize' => 10,                'pageSizeParam' => false,            ],        ]);        Yii::$app->params['list'] = $dataProvider;        return $this->render('/about/index');    }    }