<?phpnamespace frontend\controllers;use common\helpers\Visitors;use common\models\Article;use yii\web\Controller;use yii;class WenhuaController extends CommonController{    public function actionIndex()    {        parent::common();        Yii::$app->params['companyAtricle'] = Article::find()->where(['category_id'=>35])->andWhere(['status'=>1])->andWhere(['like','np','h'])->limit(3)->all();        return $this->render('/about/index',['data'=>$this->data]);    }    }