<?phpnamespace phone\controllers;use common\models\Images;use yii\web\Controller;use Yii;class SearchController extends CommonController{    public function actionIndex()    {          parent::common();        $search= Yii::$app->request->get('keyword');        //截取“电池”，“锂电池”//        var_dump($search);        if (empty($search)){            $this->view->params['search']='';        }else{            $this->view->params['search']=$search;        }        $guolv=(['锂电池','电池']);        foreach ($guolv as $key=>$value){            $search=str_replace($value,'',$search);        }        $product_list=Images::find()            ->Where(['or',['like', 'title', $search],['like', 'bianhao', $search]]);        $count=Images::find()            ->Where(['or',['like', 'title', $search],['like', 'bianhao', $search]])->select(['id'])->asArray()->all();        $c=0;        foreach ($count as $key=>$value){            $c=$key+1;        }//        var_dump($product_list);        //        1.根据编号搜索        $this->view->params['count']=$c;        $this->view->params['keyword']= Yii::$app->request->get('keyword');        $this->view->params['product_list']=$product_list;//        var_dump($product_list);        return $this->render('index',['data'=>$this->data]);    }}